let body = document.querySelector("body"),
    switchBtn = document.querySelector(".container__toggle-btn");

function switchTheme() { body.classList.toggle("light"), body.classList.toggle("dark"), populateStorage() }

function populateStorage() { localStorage.setItem("theme", body.classList.value), setStyle() }

function setStyle() { let currentTheme = localStorage.getItem("theme");
    body.classList.value = currentTheme, "light" === body.classList.value ? switchBtn.classList.add("active") : switchBtn.classList.remove("active") }
localStorage.getItem("theme") ? setStyle() : populateStorage(), switchBtn.addEventListener("click", switchTheme);